package com.networkIPS.task.data;import android.app.Application;import android.content.Context;import com.networkIPS.task.data.models.responses.GetArticlesReposeModel;import com.networkIPS.task.helpers.Config;import com.networkIPS.task.services.WebService;import io.reactivex.Observable;public class Repository {    private Context context;    Repository(Application application){        this.context = application.getApplicationContext();    }    public Observable<GetArticlesReposeModel> getArticlesReposeModelObservable(){        return WebService.getApi().getArticles(Config.MY_API_KEY);    }}