package com.networkIPS.task.data.models.items;import com.google.gson.annotations.SerializedName;import java.util.List;public class ArticleItem {    @SerializedName("id")    private long id;    @SerializedName("title")    private String title;    @SerializedName("published_date")    private String published_date;    @SerializedName("media")    private List<Media> mediaLists;    @SerializedName("byline")    private String byline;    @SerializedName("abstract")    private String abstractText;    public String getAbstractText() {        return abstractText;    }    public void setAbstractText(String abstractText) {        this.abstractText = abstractText;    }    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getPublished_date() {        return published_date;    }    public void setPublished_date(String published_date) {        this.published_date = published_date;    }    public List<Media> getMediaLists() {        return mediaLists;    }    public void setMediaLists(List<Media> mediaLists) {        this.mediaLists = mediaLists;    }    public String getByline() {        return byline;    }    public void setByline(String byline) {        this.byline = byline;    }}