package com.networkIPS.task.data.models.items;import com.google.gson.annotations.SerializedName;import java.util.List;public class Media {    @SerializedName("type")    private String type;    @SerializedName("media-metadata")    private List<mediaMetaData> mediaMetadata;    public String getType() {        return type;    }    public void setType(String type) {        this.type = type;    }    public List<mediaMetaData> getMediaMetadata() {        return mediaMetadata;    }    public void setMediaMetadata(List<mediaMetaData> mediaMetadata) {        this.mediaMetadata = mediaMetadata;    }}