package com.networkIPS.task.ui.activities.base;import androidx.fragment.app.Fragment;import com.networkIPS.task.R;public class BaseActivity extends LoadingDialogActivity {    public void showFragment(Fragment fragment, boolean back) {        if (fragment != null) {            if (back) {                getSupportFragmentManager()                        .beginTransaction()                        .replace(R.id.fragment_container, fragment)                        .addToBackStack(null)                        .commit();            } else {                getSupportFragmentManager()                        .beginTransaction()                        .replace(R.id.fragment_container, fragment)                        .commit();            }        }    }}