package com.networkIPS.task.ui.adapter.base;import android.content.Context;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.recyclerview.widget.RecyclerView;import com.networkIPS.task.helpers.GenericItemClickCallback;import java.util.ArrayList;public abstract class GenericAdapter<T> extends RecyclerView.Adapter {    private ArrayList<T> items;    private Context context;    private GenericItemClickCallback<T> adapterItemClickCallbacks;    public GenericAdapter(ArrayList<T> items,                          Context context,                          GenericItemClickCallback<T> adapterItemClickCallbacks)    {        setItems(items);        this.context = context;        this.adapterItemClickCallbacks = adapterItemClickCallbacks;    }    @Override    public void onBindViewHolder(@NonNull RecyclerView.ViewHolder holder, int position) {        if(adapterItemClickCallbacks != null){            final T item = getItem(position);            if(item != null){                holder.itemView.setOnClickListener(                        v -> adapterItemClickCallbacks.onItemClicked(item)                );            }        }    }    @Override    public int getItemCount() {        if(items == null){            return 0;        }        return items.size();    }    public void updateData(ArrayList<T> items) {        setItems(items);        notifyDataSetChanged();    }    private void setItems(ArrayList<T> is){        this.items = is;    }    public Context getContext() {        return context;    }    @Nullable    public T getItem(int position){        if(items != null && position >= 0 && position < items.size()){            return items.get(position);        }        return null;    }}