package com.networkIPS.task.ui.dialogs;import android.view.LayoutInflater;import android.view.View;import android.widget.Button;import android.widget.LinearLayout;import androidx.annotation.NonNull;import com.networkIPS.task.R;import com.networkIPS.task.ui.dialogs.base.BaseDialog;import butterknife.BindView;import butterknife.ButterKnife;public class MessageActionDialog extends BaseDialog {    private String action;    private String title_string;    private ActionClick actionClick;    private boolean loading = true;    public static final int SHOW = 0;    public static final int RETRY = 1;    public static final int HIDE = 2;    @BindView(R.id.refresh)    Button refresh;    @BindView(R.id.no_internet_connection)    LinearLayout noInternetConnection;    @BindView(R.id.loading_dialog_container_view)    View loading_dialog_container_view ;    public void setActionClick(ActionClick actionClick) {        this.actionClick = actionClick;    }    private static MessageActionDialog mInstance;    @NonNull    public static synchronized MessageActionDialog getInstance(String title, String action, ActionClick actionClick) {        MessageActionDialog messageActionDialog = new MessageActionDialog();        messageActionDialog.title_string = title;        messageActionDialog.action = action;        messageActionDialog.actionClick = actionClick;        mInstance = messageActionDialog;        return mInstance;    }    @Override    protected View createDialogView() {        View v = LayoutInflater.from(getContext()).inflate(R.layout.loading_dialog, null, false);        ButterKnife.bind(this, v);        refresh.setText(action);        refresh.setOnClickListener(v1 -> actionClick.onClick(mInstance));        loading(loading);        return v;    }    public void loading(boolean show) {        noInternetConnection.setVisibility(show ? View.GONE : View.VISIBLE);        loading_dialog_container_view.setVisibility(show ? View.VISIBLE : View.GONE);    }    public interface ActionClick {        void onClick(MessageActionDialog messageActionDialog);    }}