package com.networkIPS.task.ui.dialogs.base;import android.app.Dialog;import android.graphics.drawable.ColorDrawable;import android.os.Bundle;import android.view.View;import android.view.Window;import android.view.WindowManager;import androidx.annotation.NonNull;import androidx.fragment.app.DialogFragment;import java.util.Objects;public abstract class BaseDialog extends DialogFragment {    @NonNull    @Override    public Dialog onCreateDialog(Bundle savedInstanceState) {        Dialog dialog = new Dialog(Objects.requireNonNull(getActivity()));        dialog.requestWindowFeature(STYLE_NO_TITLE);        View v = createDialogView();        dialog.setContentView(v);        Objects.requireNonNull(dialog.getWindow()).setBackgroundDrawable(new ColorDrawable(android.graphics.Color.TRANSPARENT));        return dialog;    }    @Override    public void onStart() {        super.onStart();        WindowManager.LayoutParams lp = new WindowManager.LayoutParams();        Window window = getDialog().getWindow();        if (window != null) {            lp.copyFrom(window.getAttributes());        }        lp.height = WindowManager.LayoutParams.WRAP_CONTENT;        lp.width = WindowManager.LayoutParams.MATCH_PARENT ;        if (window != null) {            window.setAttributes(lp);        }    }    protected abstract View createDialogView();}