package com.networkIPS.task.ui.fragments;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import com.bumptech.glide.Glide;import com.networkIPS.task.R;import com.networkIPS.task.data.models.items.ArticleItem;import com.networkIPS.task.ui.fragments.base.BaseFragment;import java.util.Objects;import butterknife.BindView;import butterknife.ButterKnife;import butterknife.Unbinder;public class ArticleDetailsFragment extends BaseFragment {    @BindView(R.id.iv_report_item)    ImageView ivReportItem;    @BindView(R.id.tv_date)    TextView tvDate;    @BindView(R.id.tv_title)    TextView tvTitle;    @BindView(R.id.tv_details)    TextView tvDetails;    @BindView(R.id.tv_byline)    TextView tvByline;    private ArticleItem item;    private Unbinder unbinder;    public static ArticleDetailsFragment getInstance(ArticleItem item) {        ArticleDetailsFragment fragment = new ArticleDetailsFragment();        fragment.item = item;        return fragment;    }    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View view = inflater.inflate(R.layout.article_details, container, false);        unbinder = ButterKnife.bind(this, view);        activity().setTitle(getString(R.string.article_details));        initViews();        return view;    }    private void initViews() {        if (item != null) {            if (item.getMediaLists() != null && !item.getMediaLists().isEmpty()) {                Glide.with(Objects.requireNonNull(getContext())).load(item.getMediaLists().get(0).getMediaMetadata().get(2).getUrl()).into(ivReportItem);            }            tvDate.setText(item.getPublished_date());            tvByline.setText(item.getByline());            tvTitle.setText(item.getTitle());            tvDetails.setText(item.getAbstractText());        }    }    @Override    public void onDestroyView() {        super.onDestroyView();        if (unbinder != null) unbinder.unbind();    }}