package com.networkIPS.task.ui.fragments.base;import androidx.fragment.app.Fragment;import com.networkIPS.task.ui.activities.MainActivity;public abstract class BaseFragment extends Fragment {    protected MainActivity activity() {        MainActivity activity = null;        try {            activity = (MainActivity) getActivity();        } catch (Exception e) {        }        if (activity == null) {            activity = new MainActivity();        }        return activity;    }}